import React from "react";

const Paginate = ({ currentPage, setCurrentPage, fetchTodos, todos }) => {
  const totalPages = 3; // Replace this with your actual total pages

  const changePage = (page) => {
    if (page >= 1 && todos.length != 0) {
      setCurrentPage(page);
      fetchTodos();
    }
    // Fetch todos for the new page
  };

  return (
    <>
      <div>
        <button onClick={() => changePage(Math.max(currentPage - 1, 1))}>
          {/* // curruntPage -1 is used to reduce -1 and ..., 1 is check that the currunt page is  > 1  it will display the value between the  */}
          Prev
        </button>
        ...
        <button
          onClick={() => {
            changePage(1);
          }}
        >
          1
        </button>
        <button
          onClick={() => {
            changePage(2);
          }}
        >
          2
        </button>
        <button
          onClick={() => {
            changePage(3);
          }}
        >
          3
        </button>
        ...
        <button onClick={() => changePage(page + 1)}>Next</button>
      </div>
    </>
  );
};

export default Paginate;

