import React, { useEffect, useState } from "react";
import * as API from "../../utils/api";
import styles from "./Index.module.css";
import Paginate from "./Paginate";
import EditTodo from "./EditTodo";
import { Link } from "react-router-dom";

const Index = () => {
  const [todos, setTodos] = useState([]);

  useEffect(() => {
    fetchTodos();
  }, [state.page]);

  const fetchTodos = () => {
    console.log(state.page, state.perPage);
    API.getTodos({ page: state.page, per_page: state.perPage })
      .then((response) => {
        setTodos(response.data);
        setState((prevState) => {
          return {
            ...prevState, // Spread operator used to retain previous state
            todos: response.data, // Updating 'todos' property in the state with new data from response
          };
        });
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  const changePage = (page) => {
    setState((prevState) => {
      return {
        ...prevState, // Spread operator used to retain previous state
        page: page, // Updating 'todos' property in the state with new data from response
      };
    });
  };

  const handleCheckBoxChange = (id, checked) => {
    API.editTodo(id, { completed: checked }).then((response) => {
      setTodos((prevTodos) =>
        prevTodos.map((todo) =>
          todo.id === id ? { ...todo, completed: checked } : todo
        )
      );
    });
  };

  const totalPages = Math.ceil(todos.length / state.perPage);

  return (
    <>
      <p>{console.log(todos)}</p>
      <p>{console.log(totalPages)}</p>
      <Link to="/NewTodo" className={styles.newTodo}>
        New Todo
      </Link>
      <Link
        to="/CompleteTodo"
        className={styles.completeTodo}
        state={{ completed: todos.filter((todo) => todo.completed) }}
      >
        Completed Todo
      </Link>
      <table className={styles.table}>
        <thead className={styles.tablehead}>
          <tr>
            <th>Id</th>
            <th>Title</th>
            <th>Buttons</th>
            <th>Priority</th>
          </tr>
        </thead>
        <tbody className={styles.tablebody}>
          {todos.map((todo) => (
            <tr key={todo.id}>
              <td>{todo.id}</td>
              <td>{todo.title}</td>
              <td>
                <Link
                  to="/EditTodo"
                  styles={{ "text-decoration": "linethrough" }}
                  state={{ id: todo.id, title: todo.title }}
                >
                  Edit_{" "}
                </Link>
                <Link to="/DisplayTodo" state={{ todos: todo, id: todo.id }}>
                  Display_
                </Link>
                <input
                  type="checkbox"
                  checked={todo.completed}
                  onChange={(e) =>
                    handleCheckBoxChange(todo.id, e.target.checked)
                  }
                />
              </td>
              <td>{todo.priority}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <Paginate todos={todos}></Paginate>
    </>
  );
};

export default Index;
