import React, { useEffect, useState } from "react";
import * as API from "../../utils/api";

const Paginate = ({ todos }) => {
  const [state, setState] = useState({ todos: [], perPage: 3, page: 1 });

  useEffect(() => {
    fetchTodos();
  }, [state.page]);

  const fetchTodos = () => {
    console.log(state.page, state.perPage);
    API.getTodos({ page: state.page, per_page: state.perPage })
      .then((response) => {
        setTodos(response.data);
        setState((prevState) => {
          return {
            ...prevState, // Spread operator used to retain previous state
            todos: response.data, // Updating 'todos' property in the state with new data from response
          };
        });
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  const changePage = (page) => {
    setState((prevState) => {
      return {
        ...prevState, // Spread operator used to retain previous state
        page: page, // Updating 'todos' property in the state with new data from response
      };
    });
  };

  return (
    <>
      <div>
        <button
          onClick={() => {
            if (state.page > 1) {
              changePage(state.page - 1);
            }
          }}
        >
          Prev
        </button>
        ...
        <button
          onClick={() => {
            changePage(1);
          }}
        >
          1
        </button>
        <button
          onClick={() => {
            changePage(2);
          }}
        >
          2
        </button>
        ...
        <button
          onClick={() => {
            if (todos.length >= 1 && todos.length != 0) {
              changePage(state.page + 1);
            }
          }}
        >
          Next
        </button>
      </div>
    </>
  );
};

export default Paginate;
