import React, { useEffect, useState } from "react";
import * as API from "../../utils/api";
import styles from "./Index.module.css";
import EditTodo from "./EditTodo";
import { Link } from "react-router-dom";

const Index = () => {
  const [todos, setTodos] = useState([]);

  useEffect(() => {
    fetchTodos();
  }, []);

  const fetchTodos = () => {
    API.getTodos()
      .then((response) => {
        setTodos(response.data);
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  const handleCheckBoxChange = (id, checked) => {
    API.editTodo(id, { completed: checked });
    if (checked) {
      // If the checkbox is checked, move the todo to the completed todos list
      setTodos((prevTodos) =>
        prevTodos.map((todo) =>
          todo.id === id ? { ...todo, completed: checked } : todo
        )
      );
    } else {
      // If the checkbox is unchecked
      setTodos((prevTodos) =>
        prevTodos.map((todo) =>
          todo.id === id ? { ...todo, completed: checked } : todo
        )
      );
    }
  };

  const incompleteTodos = todos.filter((todo) => !todo.completed);

  return (
    <>
      <Link to="/NewTodo">New Todo </Link>
      <Link
        to="/CompleteTodo"
        state={{ completed: todos.filter((todo) => todo.completed) }}
      >
        Completed Todo
      </Link>
      <p>{console.log(incompleteTodos)}</p>

      <table className={styles.table}>
        <thead className={styles.tablehead}>
          <tr>
            <th>Id</th>
            <th>Title</th>
            <th>Buttons</th>
          </tr>
        </thead>
        <tbody className={styles.tablebody}>
          {todos.map((todo) => (
            <tr key={todo.id}>
              <td>{todo.id}</td>
              <td>{todo.title}</td>
              <td>
                {/* <button onClick={() => handleDelete(todo.id)}>Delete </button> */}
                {/* Above button is to Delete the Record from the database */}
                <Link to="/EditTodo" state={{ id: todo.id, title: todo.title }}>
                  Edit_{" "}
                </Link>
                <Link to="/DisplayTodo" state={{ todos: todo, id: todo.id }}>
                  Display_
                </Link>
                <input
                  type="checkbox"
                  checked={todo.completed}
                  onChange={(e) =>
                    handleCheckBoxChange(todo.id, e.target.checked)
                  }
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
};

export default Index;
