import React, { useEffect, useState } from "react";
import * as API from "../../utils/api";
import styles from "./Index.module.css";
import EditTodo from "./EditTodo";
import { Link } from "react-router-dom";

const Index = () => {
  const [todos, setTodos] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const todosPerPage = 3; // Number of todos per page

  useEffect(() => {
    fetchTodos();
  }, [currentPage]);

  const fetchTodos = () => {
    API.getTodos({ page: currentPage, per_page: todosPerPage })
      .then((response) => {
        setTodos(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const totalPages = Math.ceil(todos.length / todosPerPage);

  const handleClick = (page) => {
    setCurrentPage(page);
  };

  return (
    <>
      <p>{console.log(todos)}</p>
      <Link to="/NewTodo" className={styles.newTodo}>
        New Todo
      </Link>

      <Link
        to="/CompleteTodo"
        className={styles.completeTodo}
        state={{ completed: todos.filter((todo) => todo.completed) }}
      >
        Completed Todo
      </Link>

      <table className={styles.table}>{/* Table content */}</table>

      <div>
        <button
          onClick={() => {
            if (currentPage > 1) {
              handleClick(currentPage - 1);
            }
          }}
        >
          Prev
        </button>
        {Array.from({ length: totalPages }, (_, i) => i + 1).map((page) => (
          <button
            key={page}
            onClick={() => handleClick(page)}
            disabled={page === currentPage}
          >
            {page}
          </button>
        ))}
        <button
          onClick={() => {
            if (currentPage < totalPages) {
              handleClick(currentPage + 1);
            }
          }}
        >
          Next
        </button>
      </div>
    </>
  );
};

export default Index;
