import React, { useEffect, useState } from "react";
import * as API from "../../utils/api";
import styles from "./Index.module.css";
import NewTodo from "./NewTodo";
import EditTodo from "./EditTodo";
import DisplayTodo from "./DisplayTodo";
import { Link } from "react-router-dom";

const Index = () => {
  const [todos, setTodos] = useState([]);
  const [editingTodoId, setEditingTodoId] = useState(null);
  const [completed, setCompleted] = useState(false);

  useEffect(() => {
    fetchTodos();
  }, []);

  const fetchTodos = () => {
    API.getTodos()
      .then((response) => {
        setTodos(response.data);
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  const handleDelete = (id) => {
    API.deleteTodo(id)
      .then(() => {
        fetchTodos();
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  const handleCheckBoxChange = () => {
    setCompleted(true);
  };

  const handlecheckbox = (e) => {
    API.editTodo(state.id, { completed: completed });
  };

  const handleEdit = (id) => {
    console.log(`Editing todo with ID: ${id}`);
    setEditingTodoId(id);
  };

  return (
    <>
      <p>{console.log(todos)}</p>
      <Link to="/NewTodo">New Todo</Link>
      <Link to="/CompleteTodo">Completed Todo</Link>
      <table className={styles.table}>
        <thead className={styles.tablehead}>
          <tr>
            <th>Id</th>
            <th>Title</th>
            <th>Buttons</th>
          </tr>
        </thead>
        <tbody className={styles.tablebody}>
          {todos.map((todo) => (
            <tr key={todo.id}>
              <td>{todo.id}</td>
              <td>{todo.title}</td>
              <td>
                {/* <button onClick={() => handleDelete(todo.id)}>Delete </button> */}
                {/* Above button is to Delete the Record from the database */}

                <Link to="/EditTodo" state={{ id: todo.id, title: todo.title }}>
                  Edit_
                </Link>

                <Link to="/DisplayTodo" state={{ todos: todo, id: todo.id }}>
                  Display_
                </Link>

                <input
                  type="checkbox"
                  onClick={handlecheckbox}
                  onChange={handleCheckBoxChange}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
};

export default Index;
