import React from "react";

const Paginate = ({ currentPage, setCurrentPage, fetchTodos }) => {
  const totalPages = 3; // Replace this with your actual total pages

  const changePage = (page) => {
    setCurrentPage(page);
    fetchTodos(); // Fetch todos for the new page
  };

  return (
    <>
      <div>
        <button onClick={() => changePage(Math.max(currentPage - 1, 1))}>
          Prev
        </button>
        {[...Array(totalPages).keys()].map((page) => (
          <button key={page + 1} onClick={() => changePage(page + 1)}>
            {page + 1}
          </button>
        ))}

        <button
          onClick={() => {
            changePage(2);
          }}
        >
          2
        </button>
        <button
          onClick={() => changePage(Math.min(currentPage + 1, totalPages))}
        >
          Next
        </button>
      </div>
    </>
  );
};

export default Paginate;
