import React, { useEffect, useState } from "react";
import * as API from "../../utils/api";
import styles from "./Index.module.css";
import Paginate from "./Paginate";
import EditTodo from "./EditTodo";
import { Link } from "react-router-dom";

const Index = () => {
  const [todos, setTodos] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const perPage = 3;

  useEffect(() => {
    fetchTodos();
  }, [currentPage]);

  const fetchTodos = () => {
    console.log(currentPage, perPage);
    API.getTodos({ page: currentPage, per_page: perPage })
      .then((response) => {
        setTodos(response.data);
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  const handleCheckBoxChange = (id, checked) => {
    API.editTodo(id, { completed: checked }).then((response) => {
      setTodos((prevTodos) =>
        prevTodos.map((todo) =>
          todo.id === id ? { ...todo, completed: checked } : todo
        )
      );
    });
  };

  return (
    <>
      <p>{console.log(todos)}</p>
      <Link to="/NewTodo" className={styles.newTodo}>
        New Todo
      </Link>
      <Link
        to="/CompleteTodo"
        className={styles.completeTodo}
        state={{ completed: todos.filter((todo) => todo.completed) }}
      >
        Completed Todo
      </Link>
      <table className={styles.table}>
        <thead className={styles.tablehead}>
          <tr>
            <th>Id</th>
            <th>Title</th>
            <th>Buttons</th>
            <th>Priority</th>
          </tr>
        </thead>
        <tbody className={styles.tablebody}>
          {todos.map((todo) => (
            <tr key={todo.id}>
              <td>{todo.id}</td>
              <td>{todo.title}</td>
              <td>
                <Link
                  to="/EditTodo"
                  styles={{ "text-decoration": "linethrough" }}
                  state={{ id: todo.id, title: todo.title }}
                >
                  Edit_{" "}
                </Link>
                <Link to="/DisplayTodo" state={{ todos: todo, id: todo.id }}>
                  Display_
                </Link>
                <input
                  type="checkbox"
                  checked={todo.completed}
                  onChange={(e) =>
                    handleCheckBoxChange(todo.id, e.target.checked)
                  }
                />
              </td>
              <td>{todo.priority}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <Paginate
        currentPage={currentPage}
        setCurrentPage={setCurrentPage}
        fetchTodos={fetchTodos}
        todos={todos}
      />
    </>
  );
};

export default Index;
