import React, { useState } from "react";
import * as API from "../../utils/api";

const NewTodo = () => {
  const [title, setTitle] = useState("");

  const handleTodoCreate = () => {
    API.createTodo((param = { todo: { title: title } })).then((response) => {
      console.log(response.data);
    });
  };
  return (
    <>
      <input
        type="text"
        id="title"
        value={title}
        onChange={(val) => {
          setTitle(val);
        }}
      />
      <button
        onClick={() => {
          handleTodoCreate();
        }}
      >
        Add Todo
      </button>
    </>
  );
};

export default NewTodo;
