import React, { useEffect, useState } from "react";
import * as API from "../../utils/api";
import styles from "./Index.module.css";
import { Link } from "react-router-dom";

const Index = () => {
  const [todos, setTodos] = useState([]);

  useEffect(() => {
    fetchTodos();
  }, []);

  const handleCheckBoxChange = (id, checked) => {
    API.editTodo(id, { completed: checked })
      .then(() => {
        fetchTodos(); // Refresh todos after updating completion status
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const incompleteTodos = todos.filter((todo) => !todo.completed);

  return (
    <>
      <Link to="/NewTodo">New Todo</Link>
      <Link
        to="/CompleteTodo"
        state={{ completed: todos.filter((todo) => todo.completed) }}
      >
        Completed Todo
      </Link>

      <table className={styles.table}>
        <thead className={styles.tablehead}>
          <tr>
            <th>Id</th>
            <th>Title</th>
            <th>Buttons</th>
          </tr>
        </thead>
        <tbody className={styles.tablebody}>
          {incompleteTodos.map((todo) => (
            <tr key={todo.id}>
              <td>{todo.id}</td>
              <td>{todo.title}</td>
              <td>
                {/* <button onClick={() => handleDelete(todo.id)}>Delete </button> */}
                {/* Above button is to Delete the Record from the database */}
                <Link to={`/EditTodo?id=${todo.id}&title=${todo.title}`}>
                  Edit_
                </Link>
                <input
                  type="checkbox"
                  checked={todo.completed}
                  onChange={(e) =>
                    handleCheckBoxChange(todo.id, e.target.checked)
                  }
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
};

export default Index;
